avg_methylation = np.mean(methylation_matrix, axis=0)
avg_expression = np.mean(expression_matrix, axis=0)


data = np.vstack((avg_methylation, avg_expression)) 

# Fuzzy C-Means Clustering
n_clusters = 4
cntr, u, _, _, _, _, _ = fuzz.cluster.cmeans(
    data, c=n_clusters, m=2, error=0.005, maxiter=1000, seed=42
)

cluster_labels = np.argmax(u, axis=0)

# Determine if each sample is Normal or Tumor (even idx = Normal, odd idx = Tumor)
sample_types = ['Normal' if i % 2 == 0 else 'Tumor' for i in range(data.shape[1])]

# Set up plot settings
colors = ['r', 'g', 'b', 'm'] 
markers = {'Normal': 'o', 'Tumor': 's'}  

plt.figure(figsize=(8, 6))

for i in range(data.shape[1]):
    cluster_idx = cluster_labels[i]
    sample_type = sample_types[i]
    plt.scatter(
        data[0, i], data[1, i],
        color=colors[cluster_idx],
        marker=markers[sample_type],
        edgecolor='k',
        s=120,
        label=f"Cluster {cluster_idx+1} - {sample_type}" if f"Cluster {cluster_idx+1} - {sample_type}" not in plt.gca().get_legend_handles_labels()[1] else ""
    )

plt.xlabel("Methylation Level")
plt.ylabel("Gene Expression Level")
plt.title("Fuzzy Clustering of Samples (Normal vs Tumor)")
plt.xlim(0, 1)
plt.ylim(0, 1)
plt.grid(True)
plt.legend(bbox_to_anchor=(1.05, 1), loc='upper left')
plt.tight_layout()
plt.show()

print("\nMembership degrees for each sample:")
for i in range(u.shape[1]):
    print(f"Sample {i+1} ({sample_types[i]}):")
    for j in range(u.shape[0]):
        print(f"  Cluster {j+1}: {u[j, i]:.4f}")
    print(f"  Sum: {np.sum(u[:, i]):.4f}")
    print("------------")


